import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EmployeeEdit = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { employeeData } = location.state || {}; // Access passed employee data

  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: [],
    image: null,
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    if (employeeData) {
      setFormData({
        name: employeeData.name || '',
        email: employeeData.email || '',
        mobile: employeeData.mobile || '',
        designation: employeeData.designation || '',
        gender: employeeData.gender || '',
        course: employeeData.course ? employeeData.course.split(',') : [],
        image: null, // Reset image so the user can choose a new one
      });
    }
  }, [employeeData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      course: checked
        ? [...prev.course, value]
        : prev.course.filter((course) => course !== value),
    }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      image: e.target.files[0],
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formDataToSend = new FormData();
    
    // Append form data
    Object.keys(formData).forEach(key => {
      formDataToSend.append(key, formData[key]);
    });

    try {
      await axios.put(`http://localhost:5000/api/employees/${employeeData._id}`, formDataToSend, {
        headers: {
          'Content-Type': 'multipart/form-data', // Required for file uploads
        },
      });
      setSuccess('Employee updated successfully!');
      setError('');
      setTimeout(() => navigate('/EmployeeList'), 2000); // Redirect after 2 seconds
    } catch (error) {
      setError('Error updating employee. Please try again.');
      setSuccess('');
      console.error('Error updating employee:', error);
    }
  };

  return (
    <div className="employee-edit-form">
      <h2>Edit Employee</h2>
      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Mobile No:
          <input
            type="tel"
            name="mobile"
            value={formData.mobile}
            onChange={handleInputChange}
            pattern="[0-9]{10}"
            required
          />
        </label>
        <label>
          Designation:
          <select
            name="designation"
            value={formData.designation}
            onChange={handleInputChange}
            required
          >
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </label>
        <label>
          Gender:
          <input
            type="radio"
            name="gender"
            value="Male"
            checked={formData.gender === 'Male'}
            onChange={handleInputChange}
            required
          />
          Male
          <input
            type="radio"
            name="gender"
            value="Female"
            checked={formData.gender === 'Female'}
            onChange={handleInputChange}
            required
          />
          Female
        </label>
        <label>
          Course:
          <input
            type="checkbox"
            name="course"
            value="MCA"
            checked={formData.course.includes('MCA')}
            onChange={handleCheckboxChange}
          />
          MCA
          <input
            type="checkbox"
            name="course"
            value="BCA"
            checked={formData.course.includes('BCA')}
            onChange={handleCheckboxChange}
          />
          BCA
          <input
            type="checkbox"
            name="course"
            value="BSC"
            checked={formData.course.includes('BSC')}
            onChange={handleCheckboxChange}
          />
          BSC
        </label>
        <label>
          Image Upload:
          <input type="file" name="image" onChange={handleFileChange} />
        </label>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default EmployeeEdit;
