import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';


const EmployeeEdit = ({ employeeData }) => {

  const location = useLocation();
  const navigate = useNavigate();

  // Access the passed employee data from the location state
  // const { employeeData } = location.state;
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: [],
    image: null,
  });

  useEffect(() => {
    if (employeeData) {
      setEmployee(employeeData);
    }
  }, [employeeData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployee({
      ...employee,
      [name]: value,
    });
  };


  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    if (checked) {
      setFormData({
        ...formData,
        course: [...formData.course, value],
      });
    } else {
      setFormData({
        ...formData,
        course: formData.course.filter((course) => course !== value),
      });
    }
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      image: e.target.files[0],
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validation logic here, if any
    console.log('Updated Employee Data:', formData);
    // Submit the form data to the backend
  };

  return (
    <div className="employee-edit-form">
      <h2>Edit Employee</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Mobile No:
          <input
            type="tel"
            name="mobile"
            value={formData.mobile}
            onChange={handleInputChange}
            pattern="[0-9]{10}"
            required
          />
        </label>
        <label>
          Designation:
          <select
            name="designation"
            value={formData.designation}
            onChange={handleInputChange}
            required
          >
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </label>
        <label>
          Gender:
          <input
            type="radio"
            name="gender"
            value="Male"
            checked={formData.gender === 'Male'}
            onChange={handleInputChange}
            required
          />
          Male
          <input
            type="radio"
            name="gender"
            value="Female"
            checked={formData.gender === 'Female'}
            onChange={handleInputChange}
            required
          />
          Female
        </label>
        <label>
          Course:
          <input
            type="checkbox"
            name="course"
            value="MCA"
            checked={formData.course.includes('MCA')}
            onChange={handleCheckboxChange}
          />
          MCA
          <input
            type="checkbox"
            name="course"
            value="BCA"
            checked={formData.course.includes('BCA')}
            onChange={handleCheckboxChange}
          />
          BCA
          <input
            type="checkbox"
            name="course"
            value="BSC"
            checked={formData.course.includes('BSC')}
            onChange={handleCheckboxChange}
          />
          BSC
        </label>
        <label>
          Image Upload:
          <input type="file" name="image" onChange={handleFileChange} />
        </label>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default EmployeeEdit;
