import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [emailFilter, setEmailFilter] = useState('');
  const [idFilter, setIdFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.post('http://localhost:5000/app/getUserData', {
          // Include any necessary data you want to send in the request body
        });
        console.log('Response:', response.data);
        setEmployees(response.data);
      } catch (error) {
        console.error('Error fetching employees:', error);
      }
    };

    fetchEmployees();
  }, []);

  const routeChange = (e) => {
    e.preventDefault();
    navigate('/CreateEmployee');
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleEmailChange = (e) => {
    setEmailFilter(e.target.value);
  };

  const handleIdChange = (e) => {
    setIdFilter(e.target.value);
  };

  const handleDateChange = (e) => {
    setDateFilter(e.target.value);
  };

  // Filter employees based on name, email, ID, and date
  const filteredEmployees = employees.filter((employee) => {
    const matchesName = employee.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesEmail = employee.email.toLowerCase().includes(emailFilter.toLowerCase());
    const matchesId = employee._id.includes(idFilter);
    const matchesDate = dateFilter ? formatDate(employee.updatedAt) === dateFilter : true;

    return matchesName && matchesEmail && matchesId && matchesDate;
  });

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/app/deleteUserData/${id}`);
      setEmployees(employees.filter((employee) => employee._id !== id)); // Remove deleted employee from state
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  const handleEdit = (employee) => {
    navigate(`/EmployeeEdit`, { state: { employeeData: employee } });
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const options = { day: '2-digit', month: 'short', year: '2-digit' };
    return new Intl.DateTimeFormat('en-GB', options).format(date);
  };

  return (
    <div className="employee-list">
      <header className="header">
        <h1>Employee List</h1>
        <div className="header-controls">
          <span>Total Count: {employees.length}</span>
          <button onClick={routeChange}>Create Employee</button>
          <input
            type="text"
            placeholder="Search by Name"
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <input
            type="text"
            placeholder="Filter by Email"
            value={emailFilter}
            onChange={handleEmailChange}
          />
          <input
            type="text"
            placeholder="Filter by ID"
            value={idFilter}
            onChange={handleIdChange}
          />
          <input
            type="text"
            placeholder="Filter by Date (DD-MMM-YY)"
            value={dateFilter}
            onChange={handleDateChange}
          />
        </div>
      </header>

      <table>
        <thead>
          <tr>
            <th>Unique Id</th>
            <th>Image</th>
            <th>Name</th>
            <th>Email</th>
            <th>Mobile No</th>
            <th>Designation</th>
            <th>Gender</th>
            <th>Course</th>
            <th>Create Date</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {filteredEmployees.length > 0 ? (
            filteredEmployees.map((employee) => (
              <tr key={employee._id}>
                <td>{employee._id}</td>
                <td>
                  <img src={employee.image} alt={employee.name} width="50" height="50" />
                </td>
                <td>{employee.name}</td>
                <td>
                  <a href={`mailto:${employee.email}`}>{employee.email}</a>
                </td>
                <td>{employee.mobile}</td>
                <td>{employee.designation}</td>
                <td>{employee.gender}</td>
                <td>{employee.course.join(', ')}</td>
                <td>{formatDate(employee.updatedAt)}</td>
                <td>
                  <button onClick={() => handleEdit(employee)}>Edit</button>
                  <button onClick={() => handleDelete(employee._id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="10">No Employees Found</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default EmployeeList;
