import React, { useState } from 'react';

const CreateEmployee = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: [],
    image: null,
  });
  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setFormData({
      ...formData,
      image: e.target.files[0],
    });
  };

  const handleCheckboxChange = (e) => {
    const { value, checked } = e.target;
    let updatedCourses = [...formData.course];
    if (checked) {
      updatedCourses.push(value);
    } else {
      updatedCourses = updatedCourses.filter((course) => course !== value);
    }
    setFormData({
      ...formData,
      course: updatedCourses,
    });
  };

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name) newErrors.name = 'Name is required';
    if (!formData.email) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is not valid';
    }
    if (!formData.mobile) {
      newErrors.mobile = 'Mobile number is required';
    } else if (!/^\d+$/.test(formData.mobile)) {
      newErrors.mobile = 'Mobile number must be numeric';
    }
    if (!formData.image) {
      newErrors.image = 'Image is required';
    } else if (!/\.(jpg|png)$/i.test(formData.image.name)) {
      newErrors.image = 'Only JPG/PNG files are allowed';
    }

    setErrors(newErrors);

    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
  e.preventDefault();
  this.setState({
    message: "Submission Successful!"
  });

  if (validateForm()) {
    const formDataToSend = new FormData();
    formDataToSend.append('name', formData.name);
    formDataToSend.append('email', formData.email);
    formDataToSend.append('mobile', formData.mobile);
    formDataToSend.append('designation', formData.designation);
    formDataToSend.append('gender', formData.gender);
    formDataToSend.append('course', formData.course);
    formDataToSend.append('image', formData.image);

    try {
      const response = await fetch('http://localhost:5000/api/employees', {
        method: 'POST',
        body: formDataToSend,
      });

      const result = await response.json();
      if (response.ok) {
        console.log('Employee Created:', result);
      } else {
        console.error('Error:', result.message);
      }
    } catch (error) {
      console.error('Error submitting the form:', error);
    }
  }
};


  return (
    <div className="create-employee">
      <h2>Create Employee</h2>
      <form onSubmit={this.handleSubmit}>
        {/* Name */}
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
          />
          {errors.name && <span className="error">{errors.name}</span>}
        </div>

        {/* Email */}
        <div>
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          {errors.email && <span className="error">{errors.email}</span>}
        </div>

        {/* Mobile No */}
        <div>
          <label>Mobile No</label>
          <input
            type="text"
            name="mobile"
            value={formData.mobile}
            onChange={handleChange}
          />
          {errors.mobile && <span className="error">{errors.mobile}</span>}
        </div>

        {/* Designation Dropdown */}
        <div>
          <label>Designation</label>
          <select
            name="designation"
            value={formData.designation}
            onChange={handleChange}
          >
            <option value="">Select Designation</option>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>

        {/* Gender Radio Button */}
        <div>
          <label>Gender</label>
          <input
            type="radio"
            name="gender"
            value="M"
            onChange={handleChange}
            checked={formData.gender === 'M'}
          /> Male
          <input
            type="radio"
            name="gender"
            value="F"
            onChange={handleChange}
            checked={formData.gender === 'F'}
          /> Female
        </div>

        {/* Course Checkbox */}
        <div>
          <label>Course</label>
          <input
            type="checkbox"
            value="MCA"
            onChange={handleCheckboxChange}
            checked={formData.course.includes('MCA')}
          /> MCA
          <input
            type="checkbox"
            value="BCA"
            onChange={handleCheckboxChange}
            checked={formData.course.includes('BCA')}
          /> BCA
          <input
            type="checkbox"
            value="BSC"
            onChange={handleCheckboxChange}
            checked={formData.course.includes('BSC')}
          /> BSC
        </div>

        {/* Image Upload */}
        <div>
          <label>Image Upload</label>
          <input type="file" accept=".jpg, .png" onChange={handleFileChange} />
          {errors.image && <span className="error">{errors.image}</span>}
        </div>

        {/* Submit Button */}
        <div>
          <button type="submit" className='Submit'>Submit</button>
        </div>
        {this.state.message ? <h2>{this.state.message}</h2> : null}
      </form>
    </div>
  );
};

export default CreateEmployee;
