import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EmployeeEdit = () => {
  const location = useLocation();
  const navigate = useNavigate();

  // Access the passed employee data from the location state
  const { employeeData } = location.state;

  // State for holding the employee data to edit
  const [employee, setEmployee] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: '',
    image: '',
  });

  // Load the employee data into the form when the component mounts
  useEffect(() => {
    if (employeeData) {
      setEmployee(employeeData);
    }
  }, [employeeData]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEmployee({
      ...employee,
      [name]: value,
    });
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send updated employee data to the server (use the correct API URL)
      await axios.put(`http://localhost:5000/api/employees/${employee._id}`, employee);
      alert('Employee updated successfully!');
      navigate('/'); // Navigate back to the employee list after updating
    } catch (error) {
      console.error('Error updating employee:', error);
      alert('Error updating employee');
    }
  };

  return (
    <div className="employee-edit">
      <h1>Edit Employee</h1>
      <form onSubmit={handleFormSubmit}>
        <label>
          Name:
          <input
            type="text"
            name="name"
            value={employee.name}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={employee.email}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Mobile:
          <input
            type="text"
            name="mobile"
            value={employee.mobile}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Designation:
          <input
            type="text"
            name="designation"
            value={employee.designation}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Gender:
          <input
            type="text"
            name="gender"
            value={employee.gender}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Course:
          <input
            type="text"
            name="course"
            value={employee.course}
            onChange={handleInputChange}
            required
          />
        </label>
        <label>
          Image URL:
          <input
            type="text"
            name="image"
            value={employee.image}
            onChange={handleInputChange}
          />
        </label>
        <button type="submit">Update Employee</button>
      </form>
    </div>
  );
};

export default EmployeeEdit;
